function histo4data(data1, data2, data3, data4)
%CREATEFIGURE(DATA1, DATA2, DATA3, DATA4)
%  DATA1:  histogram data
%  DATA2:  histogram data
%  DATA3:  histogram data
%  DATA4:  histogram data

%  Auto-generated by MATLAB on 24-Dec-2016 00:47:45

% Create figure
figure1 = figure('InvertHardcopy','off','Color',[1 1 1],...
    'Renderer','painters');

% Create axes
axes1 = axes('Parent',figure1,'LineWidth',1.5,'FontSize',16,...
    'FontName','arial');
%% Uncomment the following line to preserve the X-limits of the axes
% xlim(axes1,[4 8]);
%% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[0 16000]);
%% Uncomment the following line to preserve the Z-limits of the axes
% zlim(axes1,[-1 1]);
box(axes1,'on');
hold(axes1,'on');

% Create histogram
histogram(data1,'DisplayName','Prebleaching','Parent',axes1,...
    'EdgeColor',[0 0 0],...
    'FaceColor','auto',...
    'Normalization','count',...
    'BinLimits',[4 8],...
    'BinWidth',0.02);

% Create histogram
histogram(data2,'DisplayName','1st bleaching','Parent',axes1,...
    'EdgeColor',[0 0 0],...
    'FaceColor',[1 1 0],...
    'Normalization','count',...
    'BinLimits',[4 8],...
    'BinWidth',0.02);

% Create histogram
histogram(data3,'DisplayName','2nd bleaching','Parent',axes1,...
    'EdgeColor',[0 0 0],...
    'FaceColor',[1 0 0],...
    'Normalization','count',...
    'BinLimits',[4 8],...
    'BinWidth',0.02);

% Create histogram
histogram(data4,'DisplayName','3rd bleaching','Parent',axes1,...
    'EdgeColor',[0 0 0],...
    'FaceColor',[0 1 0],...
    'Normalization','count',...
    'BinLimits',[4 8],...
    'BinWidth',0.02);

% Create xlabel
xlabel('log(Intensity)');

% Create ylabel
ylabel('Cell counts');

% Create title
title('FITC/Alexa488');

% Create legend
legend1 = legend(axes1,'show');
set(legend1,...
    'Position',[0.574404768237755 0.669841276085566 0.312499993667006 0.226984120739831]);

